
public class TrianguloInvertido {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int col1 = 6; col1 >= 1; col1--) {        
		for (int col2 = 1; col2 <= col1; col2++) {
		           
		System.out.print("* ");
		}

		System.out.println();
		        }
		    }
		}

		
		
		
